import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Trade } from '../../models/trade.model';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-trade-blotter',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="panel">
      <div class="panel-header">
        TRADE BLOTTER
        <div class="blotter-controls">
          <span class="trade-count">{{ trades.length }} TRADES</span>
          <button class="clear-btn" (click)="clearTrades()">CLEAR</button>
        </div>
      </div>
      <div class="panel-content">
        <div class="blotter-table-container">
          <table class="blotter-table">
            <thead>
              <tr>
                <th>TIME</th>
                <th>CUSIP</th>
                <th>MATURITY</th>
                <th>SIDE</th>
                <th>QTY</th>
                <th>PRICE</th>
                <th>YIELD</th>
                <th>COUNTERPARTY</th>
                <th>TRADER</th>
                <th>STATUS</th>
                <th>SETTLE</th>
              </tr>
            </thead>
            <tbody>
              <tr *ngFor="let trade of trades; trackBy: trackByTrade" 
                  [class]="getTradeRowClass(trade)"
                  [class.blink]="isNewTrade(trade.id)">
                <td class="time">{{ formatTime(trade.timestamp) }}</td>
                <td class="cusip">{{ trade.cusip }}</td>
                <td class="maturity">{{ trade.maturity }}</td>
                <td class="side" [class]="getSideClass(trade.side)">{{ trade.side }}</td>
                <td class="quantity">{{ formatQuantity(trade.quantity) }}</td>
                <td class="price">{{ formatPrice(trade.price) }}</td>
                <td class="yield">{{ formatPercent(trade.yield) }}</td>
                <td class="counterparty">{{ trade.counterparty }}</td>
                <td class="trader">{{ trade.trader }}</td>
                <td class="status" [class]="getStatusClass(trade.status)">{{ trade.status }}</td>
                <td class="settle">{{ formatSettleDate(trade.settlementDate) }}</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .panel-header {
      display: flex;
      justify-content: space-between;
      align-items: center;
    }

    .blotter-controls {
      display: flex;
      gap: 10px;
      align-items: center;
    }

    .trade-count {
      font-size: 10px;
      color: var(--text-secondary);
    }

    .clear-btn {
      background: var(--bg-secondary);
      border: 1px solid var(--border-color);
      color: var(--text-primary);
      padding: 2px 8px;
      font-size: 10px;
      cursor: pointer;
      font-family: var(--font-mono);
    }

    .clear-btn:hover {
      background: var(--bg-hover);
    }

    .blotter-table-container {
      max-height: 300px;
      overflow-y: auto;
      border: 1px solid var(--border-color);
    }

    .blotter-table {
      width: 100%;
      border-collapse: collapse;
      font-size: 10px;
      font-family: var(--font-mono);
    }

    .blotter-table th {
      background: var(--bg-secondary);
      padding: 4px 6px;
      text-align: left;
      border-bottom: 1px solid var(--border-color);
      position: sticky;
      top: 0;
      z-index: 1;
    }

    .blotter-table td {
      padding: 2px 6px;
      border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    }

    .blotter-table tr:hover {
      background: var(--bg-hover);
    }

    .time {
      color: #888;
      width: 60px;
    }

    .cusip {
      font-weight: bold;
      width: 80px;
    }

    .maturity {
      width: 60px;
    }

    .side {
      font-weight: bold;
      text-align: center;
      width: 40px;
    }

    .side.buy {
      color: var(--positive-color);
    }

    .side.sell {
      color: var(--negative-color);
    }

    .quantity {
      text-align: right;
      width: 80px;
    }

    .price {
      text-align: right;
      font-weight: bold;
      width: 70px;
    }

    .yield {
      text-align: right;
      color: var(--text-secondary);
      width: 60px;
    }

    .counterparty {
      width: 100px;
    }

    .trader {
      width: 80px;
      color: var(--text-accent);
    }

    .status {
      text-align: center;
      font-weight: bold;
      width: 70px;
    }

    .status.executed {
      color: var(--positive-color);
    }

    .status.pending {
      color: var(--text-warning);
    }

    .status.cancelled, .status.failed {
      color: var(--negative-color);
    }

    .settle {
      width: 70px;
      font-size: 9px;
    }

    .trade-executed {
      background: rgba(0, 255, 0, 0.05);
    }

    .trade-cancelled {
      background: rgba(255, 0, 0, 0.05);
      opacity: 0.7;
    }

    .trade-failed {
      background: rgba(255, 0, 0, 0.1);
    }

    .blink {
      animation: blink 0.5s ease-in-out;
    }

    @keyframes blink {
      0%, 100% { background-color: transparent; }
      50% { background-color: var(--text-accent); }
    }
  `]
})
